import React from 'react';
import { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { useLocation } from 'react-router-dom';

import TopPage from './components/TopPage';
import PrivacyPolicy from './components/PrivacyPolicy';
import Header from './components/Header';
import Footer from './components/Footer';


function App() {
  return (
    <div style={{
      backgroundColor: 'dimgray',
      color: 'white',
      minHeight: '100vh',
    }}>
      <Router>
      <PageViewTracker /> {/* ここでPageViewTrackerコンポーネントを使用 */}
        <Routes>
          <Route path="/" element={<><Header /><TopPage /><Footer /></>} />
          <Route path="/privacy-policy" element={<><Header /><PrivacyPolicy /><Footer /></>} />
        </Routes>
      </Router>
    </div>
  );
}

// 新しいPageViewTrackerコンポーネントを作成
function PageViewTracker() {
  let location = useLocation();

  useEffect(() => {
    if (!window.gtag) return;
    window.gtag('config', 'G-PR4L22G5TW', {
      'page_path': location.pathname,
    });
  }, [location]);

  return null; // 何も描画しない
}

export default App;
